import { configureStore, createSlice } from '@reduxjs/toolkit'

const user= createSlice({
    name: 'user',
    initialState: {id : 'abc', age: 22},
    reducers: {
        changeId(state){
            state.id = 'abc1234'
        },
    
        addAge(state){
        ++state.age;
        },
        
    }
})


export const {changeId, addAge} = user.actions

const cart = createSlice({
    name: 'cart',
    initialState: [
        {id:0, item: 'white and Black', amount: 1},
        {id:1, item: 'Red knit', amount: 2}
    ],
    reducers:{
        addCount(state, action){
            let idx = state.findIndex(a => a.id === action.payload)

            ++state[idx].amount;
        },
        sortItem(state){
            state.sort((a,b)=>
                a.item > b.item ? 1: -1
            )
        },
        addItem(state){
            state.push({id,item,amount,price})
        }
    }
})

export const {addCount, sortItem} = cart.actions;

export default configureStore({
  reducer: {
    user: user.reducer,
    cart: cart.reducer
  }
})
