import React from 'react'
import {Container, Nav, Navbar, Row, Button} from 'react-bootstrap';
import Product from '../components/Product';
import Title from '../components/Title';

const Home = (props) => {
    const {shoes, setShoes, no, setNo} = props
  return (
    <>
     
      <div className="slider"></div>

     <Title />

      <Button variant="outline-primary" onClick={()=>{
        let copy = [...shoes].sort((a,b)=>
          (a.title > b.title) ? 1 : -1
        )

        setShoes(copy)

      }}>이름순으로 정렬</Button>{' '}
      <Button variant="outline-secondary">낮은가격순 정렬</Button>{' '}
      <Button variant="outline-success">높은가격순 정렬</Button>
      

      <Container>
      <Row>
        {
          shoes.map((value, i)=>
              <Product shoes ={shoe} key={i} i={i} no={no}
            />
            )
          }
        
      </Row>
    </Container>
    </>
  )
}

export default Home
