import { configureStore, createSlice } from '@reduxjs/toolkit'

const user= createSlice({
    name: 'user',
    initialState: {id : 'abc', age: 22},
    reducers: {
        changeId(state){
            state.id = 'abc1234'
        },
    
        addAge(state){
        state.age += action.payload
        }
    }
})

export const {changeId, increase} = user.actions

export default configureStore({
  reducer: {
    user: user.reducer
  }
})
