import { configureStore, createSlice } from '@reduxjs/toolkit'

const user = createSlice({
    name: 'user',
    initialState: {name: '유비', age: 25},
    reducers : {
        changeName(state){
            if(state.name=='유비'){
                state.name = '관우'
            }else {
                state.name = '유비'
            }
        }
    }
})

export const {changeName} = user.actions;

const cart = createSlice({
    name: 'cart',
    initialState: [{id: 1, item:'tomato', amount: 2}]
})

export default configureStore({
  reducer: {
    user: user.reducer,
    cart : cart.reducer
  }
})
