import React, { useEffect, useState } from 'react'

const TabContent = (props) => {
    const {tab} = props
    const [fade, setFade] = useState('')

    useEffect(()=>{
        setFade('end')
    },[tab])
  return (
    <div className={'start' + fade}>
        {
          [<div>내용0</div>,<div>내용1</div>,<div>내용2</div>][tab]
        }
    </div>
  )
}

export default TabContent
