import { configureStore, createSlice } from '@reduxjs/toolkit'

const user = createSlice({
    name: 'user',
    initialState: {name: '유비', age: 25},
    reducers : {
        changeName(state){
            state.name=='유비'? state.name = '관우'
            : state.name='유비'
        },
        changeAge(state, action){

            state.age += action.payload
        }
        
    }
})

export const {changeName,changeAge } = user.actions;

let cart = createSlice({
    name: 'cart',
    initialState: [
    {id: 0, item:'tomato', amount: 2},
    {id: 1, item:'watermelon', amount: 1},
    {id: 1, item:'watermelon', amount: 1}
    ],
    reducers: {
        addCount(state, action){
            let idx = action.payload
            ++state[idx].amount
        },
        sortItem(state){
            state.sort((a,b)=>
                a.item > b.item ? 1: -1
            )
        }
    }
})

export const {addCount, sortItem} = cart.actions;

export default configureStore({
  reducer: {
    user : user.reducer,
    cart : cart.reducer
  }
})
