import React, { useEffect, useState } from 'react'
import {Col, Container, Row, Button, Nav, Alert} from 'react-bootstrap'
import { useParams } from 'react-router-dom'

const Detail = (props) => {
    const {shoes} = props
    let{id} = useParams();
    const [tab, setTab] = useState(0);
    let selproduct = shoes.find(x => x.id == id)

    useEffect(()=>{
      
    })

  return (
    <Container>
      <Alert variant={'warning'}>
          2초 이내 구매시 할인
        </Alert>
        <Row>
            <Col md={6}>
            <img src={selproduct.imgUrl} width="100%" alt="shoes" />
            </Col>
            <Col md={6}>
                <h4 className='pt-5'>{selproduct.title}</h4>
                <p>{shoes[id].content}</p>
               <p>{shoes[id].price}상품가격</p>
               <Button variant="warning">Warning</Button>
            </Col>
        </Row>

        <Nav variant="tabs" defaultActiveKey="link-0">
      <Nav.Item>
        <Nav.Link eventKey="link-0" onClick={()=>{
          setTab(0)
        }}>버튼0</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-1" onClick={()=>{
          setTab(1)
        }}>버튼1</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-2" onClick={()=>{
          setTab(2)
        }}>
          버튼2</Nav.Link>
      </Nav.Item>
    </Nav>

    {
      // (()=>{
      //   if(tab === 0 ){
      //     return <div>내용0</div>
      //   }else if(tab === 1){
      //     return <div> 내용1</div>
      //   }else if(tab ===2) {
      //     return <div>내용2</div>
      //   }
      // })()
      [<div>내용0</div>,<div>내용1</div>,<div>내용2</div>][tab]
      
    }

    {/* <div>내용0</div>
    <div>내용1</div>
    <div>내용2</div> */}

    </Container>


  )
}

export default Detail
