import { configureStore, createSlice } from '@reduxjs/toolkit'

const user = createSlice({
    name: 'user',
    initialState: 'kim'
})

const cart = createSlice({
    name: 'cart',
    initialState: [{id: 1, item:'tomato', amount: 2}]
})

export default configureStore({
  reducer: {
    user: user.reducer
  }
})
