import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { changeName, changeAge, addCount, sortItem, addItem } from '../store'

const Cart = () => {
    const user = useSelector( state => state.user )
   const cart = useSelector(state => state.cart)

   const {id, item, amount} = cart

   const dispatch = useDispatch()
    
  return (
    <div>
        <h5>{user.name + ''+ user.age}의 장바구니</h5>
        <button onClick={()=>{
            dispatch(changeName())
        }}> 이름바꾸기</button>
        <button onClick={()=>{
            dispatch(changeAge(1))
        }}>나이+</button>
        <button onClick={()=>{
            dispatch(changeAge(-1))
        }}>나이-</button>
    

        <table style={{margin:'50px auto', width: '80%'}}>
            <thead>
                <tr>
                    <th>id</th>
                    <th>상품명</th>
                    <th>수량</th>
                    <th>변경하기</th>
                </tr>
            </thead>
            <tbody>
               {
                cart.map((value, i) => {
                    return(
                        <tr key={i}>
                            <td>{value.id}</td>
                            <td>{value.item}</td>
                            <td>{value.amount}</td>
                            <td><button onClick={()=>{
                                dispatch(addCount(value.id))
                            }}>+</button></td>
                        </tr>
                    )
                })
               }
            </tbody>
        </table>
        <button onClick={()=>{
        dispatch(sortItem())
        }}>상품정렬</button>
        
        <Order/>
    </div>

  )
}

const Order = () =>{
    const dispatch = useDispatch();
    return(
        <div style={{marginTop: '50px'}}>
            <hr />
            <h4>주문하기 페이지</h4>
            <button onClick={()=>{
                dispatch(addItem({id: 2, item: 'orange', amount: 1}))
            }}>오렌지 주문하기</button>
        </div>
    )
}

export default Cart
