import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { changeName, decrease, increase, changeAge } from '../store'

const Cart = () => {
    const user = useSelector( state => state.user )
    console.log(user.name)

   const dispatch = useDispatch()
    
  return (
    <div>
        <h5>{user.name + ''+ user.age}의 장바구니</h5>
        <button onClick={()=>{
            dispatch(changeName())
        }}> 이름바꾸기</button>
        <button onClick={()=>{
            dispatch(changeAge(1))
        }}>나이+</button>
        <button onClick={()=>{
            dispatch(changeAge(-1))
        }}>나이-</button>
    </div>
  )
}

export default Cart
