import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { changeName, changeAge, addCount } from '../store'

const Cart = () => {
    const user = useSelector( state => state.user )
   const cart = useSelector(state => state.cart)

   const {id, item, amount} = cart

   const dispatch = useDispatch()
    
  return (
    <div>
        <h5>{user.name + ''+ user.age}의 장바구니</h5>
        <button onClick={()=>{
            dispatch(changeName())
        }}> 이름바꾸기</button>
        <button onClick={()=>{
            dispatch(changeAge(1))
        }}>나이+</button>
        <button onClick={()=>{
            dispatch(changeAge(-1))
        }}>나이-</button>
    

        <table style={{margin:'50px auto', width: '80%'}}>
            <thead>
                <tr>
                    <th>id</th>
                    <th>상품명</th>
                    <th>수량</th>
                    <th>변경하기</th>
                </tr>
            </thead>
            <tbody>
               {
                cart.map((value, i) => {
                    return(
                        <tr>
                            <td>{value.id}</td>
                            <td>{value.item}</td>
                            <td>{value.amount}</td>
                            <td><button onClick={()=>{
                                dispatch(addCount(value.id))
                            }}>+</button></td>
                        </tr>
                    )
                })
               }
            </tbody>
        </table>
    </div>

  )
}

export default Cart
